import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './pages/User/Home';
import Services from './pages/User/Services';
import Navbar from './components/Navbar';
import About from './pages/User/About';
import Clients from './pages/User/Clients';
import Contact from './pages/User/Contact';
import Profile from './pages/User/Profile';
import Booking from './pages/User/Booking';
import Store from './pages/User/Store';
import Notification from './pages/User/Notification';
import StylistDashboard from './pages/Stylist/StylistDashboard';
import Modal from './components/Modal';

function App() {
    const [modalOpen, setModalOpen] = useState(false);

    const isAuthenticated = () => !!localStorage.getItem('token');
    const isStylist = () => {
        const storedUser = localStorage.getItem('user');
        if (!storedUser) return false; // No user, not a stylist
        try {
            const user = JSON.parse(storedUser);
            return user.role === 'stylist';
        } catch (error) {
            console.error('Invalid user data in localStorage:', error);
            return false; // Invalid JSON, assume not a stylist
        }
    };

    return (
        <Router>
            <Navbar openModal={() => setModalOpen(true)} />
            <Routes>
                <Route path="/" element={<Home openModal={() => setModalOpen(true)} />} />
                <Route path="/services" element={<Services />} />
                <Route path="/user/about" element={<About />} />
                <Route path="/user/clients" element={<Clients />} />
                <Route path="/user/contact" element={<Contact />} />
                <Route
                    path="/profile"
                    element={isAuthenticated() ? <Profile /> : <Navigate to="/" />}
                />
                <Route
                    path="/booking"
                    element={isAuthenticated() ? <Booking /> : <Navigate to="/" />}
                />
                <Route
                    path="/store"
                    element={isAuthenticated() ? <Store /> : <Navigate to="/" />}
                />
                <Route
                    path="/notifications"
                    element={isAuthenticated() ? <Notification /> : <Navigate to="/" />}
                />
                <Route
                    path="/stylist-dashboard"
                    element={
                        isAuthenticated() && isStylist() ? (
                            <StylistDashboard />
                        ) : (
                            <Navigate to="/" />
                        )
                    }
                />
            </Routes>
            <Modal
                isOpen={modalOpen}
                closeModal={() => setModalOpen(false)}
            />
        </Router>
    );
}

export default App;
