// src/components/Navbar.js
import React from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import '../styles/Navbar.css';

// Import icons from assets folder
import menuIcon from '../assets/icons/menu.png';
import cartIcon from '../assets/icons/shopping-cart.png';
import userIcon from '../assets/icons/user.png';
import moonLogo from '../assets/icons/moon.svg';

const Navbar = ({ openModal }) => {
  const navigate = useNavigate();
  const location = useLocation();

  // Clicking on the brand name (Moon's Saloon)
  const handleLogoClick = () => {
    if (location.pathname === '/') {
      window.location.reload(); // Refresh the page if already on home
    } else {
      navigate('/'); // Navigate to home page
    }
  };

  return (
    <nav className="navbar">
      {/* Left Side: Menu Dropdown */}
      <div className="nav-left">
        <div className="dropdown-container">
          <img src={menuIcon} alt="Menu Icon" className="menu-icon" />
          <ul className="dropdown-menu">
            <li>
            <Link to="/services">Services</Link> {/* Redirects to Services Page */}
            </li>
            <li>
            <Link to="/user/about">About Us</Link>  {/* âœ… Correct Path */}
            </li>
            <li>
            <Link to="/user/clients">Our Clients</Link>
            </li>
            <li>
              <Link to="/user/contact">Contact Info</Link>
            </li>
          </ul>
        </div>
      </div>

      {/* Center: Moon's Saloon Title */}
      <div className="nav-center" onClick={handleLogoClick} style={{ cursor: 'pointer' }}>
        <img src={moonLogo} alt="Moon's Salon Logo" className="nav-logo" />
      </div>

      {/* Right Side: Store and Profile Icons */}
      <div className="nav-right">
        <Link to="/store" className="icon-link">
          <img src={cartIcon} alt="Store Icon" className="nav-icon" />
        </Link>
        {/* Clicking the profile icon opens the login/signup modal */}
        <div onClick={openModal} className="icon-link" style={{ cursor: 'pointer' }}>
          <img src={userIcon} alt="Profile Icon" className="nav-icon" />
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
