// src/components/Navbar.js
import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import '../styles/Navbar.css';

// Local icons
import menuIcon from '../assets/icons/menu.png';
import cartIcon from '../assets/icons/shopping-cart.png';
import userIcon from '../assets/icons/user.png';

const Navbar = ({ openModal }) => {
  const navigate = useNavigate();
  const location = useLocation();

  // Logo text behavior (click to go home/refresh if already there)
  const handleLogoClick = () => {
    if (location.pathname === '/') {
      window.location.reload(); 
    } else {
      navigate('/');
    }
  };

  return (
    <nav className="navbar">
      {/* Left: Menu (dropdown) */}
      <div className="nav-left">
        <div className="dropdown-container">
          <img src={menuIcon} alt="Menu Icon" className="menu-icon" />
          <ul className="dropdown-menu">
            <li><a href="/services">Services</a></li>
            <li><a href="/about-us">About Us</a></li>
            <li><a href="/clients">Our Clients</a></li>
            <li><a href="/contact">Contact Info</a></li>
          </ul>
        </div>
      </div>

      {/* Center: Moon’s Saloon clickable title */}
      <div className="nav-center" onClick={handleLogoClick} style={{ cursor: 'pointer' }}>
        Moon’s Saloon
      </div>

      {/* Right: Store & Profile */}
      <div className="nav-right">
        <a href="/store" className="icon-link">
          <img src={cartIcon} alt="Store Icon" className="nav-icon" />
        </a>
        {/* Clicking profile opens the modal */}
        <div onClick={openModal} className="icon-link" style={{ cursor: 'pointer' }}>
          <img src={userIcon} alt="Profile Icon" className="nav-icon" />
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
