import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../../styles/AdminDashboard.css';

const AdminDashboard = () => {
  const [services, setServices] = useState([]);
  const [formData, setFormData] = useState({ name: '', description: '', priceRange: '', image: null });
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchServices();
  }, []);

  const fetchServices = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/services/all');
      setServices(res.data.map(service => ({
        ...service,
        imageUrl: service.imageUrl || 'default-image-url.jpg' // Provide a default image URL if none exists
      })));
    } catch (error) {
      console.error('Error fetching services:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    Object.keys(formData).forEach((key) => data.append(key, formData[key]));

    try {
      if (editId) {
        await axios.put(`http://localhost:5000/api/services/${editId}`, data, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
        setEditId(null);
      } else {
        await axios.post('http://localhost:5000/api/services', data, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
      }
      setFormData({ name: '', description: '', priceRange: '', image: null });
      fetchServices();
    } catch (error) {
      console.error('Error saving service:', error);
      alert('Failed to save service. Please try again.');
    }
  };

  const handleEdit = (service) => {
    setEditId(service._id);
    setFormData({ name: service.name, description: service.description, priceRange: service.priceRange, image: null });
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this service?')) {
      try {
        await axios.delete(`http://localhost:5000/api/services/${id}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
        fetchServices();
      } catch (error) {
        console.error('Error deleting service:', error);
        alert('Failed to delete service. Please try again.');
      }
    }
  };

  return (
    <div className="admin-dashboard">
      <h1>Manage Services</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Name"
          value={formData.name}
          onChange={(e) => setFormData({ ...formData, name: e.target.value })}
          required
        />
        <textarea
          placeholder="Description"
          value={formData.description}
          onChange={(e) => setFormData({ ...formData, description: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Price Range (e.g., Starting from $50)"
          value={formData.priceRange}
          onChange={(e) => setFormData({ ...formData, priceRange: e.target.value })}
          required
        />
        <input
          type="file"
          onChange={(e) => setFormData({ ...formData, image: e.target.files[0] })}
        />
        <button type="submit">{editId ? 'Update' : 'Add'} Service</button>
      </form>

      <div className="services-list">
        {services.map((service) => (
          <div key={service._id} className="service-item">
            <img src={service.imageUrl} alt={service.name} />
            <h3>{service.name}</h3>
            <p>{service.priceRange}</p>
            <button onClick={() => handleEdit(service)}>Edit</button>
            <button onClick={() => handleDelete(service._id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminDashboard;
