import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../../styles/StylistDashboard.css';

const StylistDashboard = () => {
  const navigate = useNavigate();
  const [userInfo, setUserInfo] = useState(null); // Store user data
  const [error, setError] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('Please log in to access the dashboard.');
      navigate('/');
      return;
    }

    // Fetch user profile using existing /api/auth/me endpoint
    axios
      .get('http://localhost:5001/api/auth/me', {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((response) => {
        setUserInfo(response.data); // Set the full user object
        setError('');
      })
      .catch((error) => {
        console.error('Error fetching profile:', error);
        setError('Failed to load profile. Please try again.');
        if (error.response?.status === 401) {
          localStorage.removeItem('token');
          navigate('/');
        }
      });
  }, [navigate]);

  if (!userInfo) {
    return <p>Loading...</p>;
  }

  return (
    <div className="stylist-dashboard">
      <h1>Welcome, {userInfo.username}!</h1>
      {error && <p className="error">{error}</p>}

      <div className="profile-section">
        <h2>Your Profile</h2>
        <p><strong>Username:</strong> {userInfo.username}</p>
        <p><strong>Email:</strong> {userInfo.email}</p>
        <p><strong>Role:</strong> {userInfo.role}</p>
        <button onClick={() => { localStorage.removeItem('token'); navigate('/'); }} className="logout-button">
          Logout
        </button>
      </div>
    </div>
  );
};

export default StylistDashboard;
