import React from 'react';
import Navbar from '../../components/Navbar';
import Footer from '../../components/Footer';
import '../../styles/Clients.css';

import client1 from '../../assets/images/Client1.png';
import client2 from '../../assets/images/client2.jpg';
import client3 from '../../assets/images/client3.jpg';



const clientReviews = [
    {
        name: "Sophia Carter",
        review: "Amazing service! The stylists are professional and friendly. Highly recommend!",
        image: "Client1"
    },
    {
        name: "Michael Brown",
        review: "Great atmosphere and top-notch service. My go-to place for haircuts!",
        image: "Client2"
    },
    {
        name: "Emily Johnson",
        review: "Loved my experience here! The salon is so elegant and the staff is wonderful.",
        image: "Client3"
    }
];

const Clients = () => {
    return (
        <div className="clients-page">
            <Navbar />
            <div className="clients-header">
                <h1>Our Happy Clients</h1>
                <p>See what our clients have to say about their experience at Moon's Salon.</p>
            </div>

            <div className="client-reviews">
                {clientReviews.map((client, index) => (
                    <div className="review-card" key={index}>
                        <img src={client.image} alt={client.name} className="client-image" />
                        <h3>{client.name}</h3>
                        <p>"{client.review}"</p>
                    </div>
                ))}
            </div>

            <Footer />
        </div>
    );
};

export default Clients;
