import React from 'react';
// Assuming you have Navbar and Footer components; if not, remove these imports and their usage
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import '../styles/Notification.css';

const Notification = () => {
    // Dummy data for now; replace with real data from your backend later
    const notifications = [
        { id: 1, message: 'Your booking for Haircut & Styling is confirmed.', date: '2025-03-15' },
        { id: 2, message: 'New product available in the store.', date: '2025-03-14' },
    ];

    return (
        <div className="notification-page">
            <Navbar />
            <div className="notification-header">
                <h1>Notifications</h1>
            </div>
            <div className="notification-list">
                {notifications.length === 0 ? (
                    <p>No notifications available.</p>
                ) : (
                    notifications.map((notification) => (
                        <div key={notification.id} className="notification-item">
                            <p>{notification.message}</p>
                            <span>{notification.date}</span>
                        </div>
                    ))
                )}
            </div>
            <Footer />
        </div>
    );
};

export default Notification;
