import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/Navbar.css';
import menuIcon from '../assets/icons/menu.png';
import cartIcon from '../assets/icons/shopping-cart.png';
import userIcon from '../assets/icons/user.png';
import moonLogo from '../assets/icons/moon.svg';

const Navbar = ({ openModal }) => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);

    const updateUserState = () => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            setUser(JSON.parse(storedUser));
        } else {
            setUser(null);
        }
    };

    useEffect(() => {
        // Initial load
        updateUserState();

        // Listen for auth changes
        window.addEventListener('authChange', updateUserState);

        // Cleanup listener on unmount
        return () => {
            window.removeEventListener('authChange', updateUserState);
        };
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        setUser(null);
        navigate('/');
        window.dispatchEvent(new Event('authChange')); // Notify on logout too
    };

    const handleProfileClick = () => {
        if (user) {
            navigate('/profile');
        } else {
            openModal(updateUserState); // Pass update function to modal
        }
    };

    return (
        <nav className="navbar">
            <div className="nav-left">
                <div className="dropdown-container">
                    <img src={menuIcon} alt="Menu Icon" className="menu-icon" />
                    <ul className="dropdown-menu">
                        <li><Link to="/services">Services</Link></li>
                        <li><Link to="/user/about">About Us</Link></li>
                        <li><Link to="/user/clients">Our Clients</Link></li>
                        <li><Link to="/user/contact">Contact Info</Link></li>
                    </ul>
                </div>
            </div>

            <div className="nav-center">
                <img
                    src={moonLogo}
                    alt="Moon's Salon Logo"
                    className="nav-logo"
                    onClick={() => navigate('/')}
                    style={{ cursor: 'pointer' }}
                />
            </div>

            <div className="nav-right">
                <Link to="/store" className="icon-link">
                    <img src={cartIcon} alt="Store Icon" className="nav-icon" />
                </Link>

                <div onClick={handleProfileClick} className="icon-link" style={{ cursor: 'pointer' }}>
                    <img src={userIcon} alt="Profile Icon" className="nav-icon" />
                </div>

                {user && (
                    <button onClick={handleLogout}>Logout</button>
                )}
            </div>
        </nav>
    );
};

export default Navbar;
