import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import '../../styles/Service.css';

const Services = () => {
  const [services, setServices] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetchServices();
  }, []);

  const fetchServices = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Please log in to view services.');
        navigate('/'); // Redirect to login if not authenticated
        return;
      }

      const res = await axios.get('http://localhost:5000/api/services', {
        headers: { Authorization: `Bearer ${token}` },
      });

      setServices(res.data.map(service => ({
        ...service,
        imageUrl: service.imageUrl || 'https://via.placeholder.com/150', // Placeholder image
      })));
      setError('');
    } catch (error) {
      console.error('Error fetching services:', error);
      if (error.response) {
        console.error('Response data:', error.response.data);
        console.error('Response status:', error.response.status);
      }
      setError(error.response?.data?.message || 'Failed to fetch services. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="services-container">
      <h1>Available Services</h1>
      {error && <div className="error-message">{error}</div>}
      {loading ? (
        <div className="loading-message">Loading services...</div>
      ) : (
        <div className="services-list">
          {services.length === 0 ? (
            <p>No services available.</p>
          ) : (
            services.map((service) => (
              <div key={service._id} className="service-card">
                <img src={service.imageUrl} alt={service.name} className="service-image" />
                <h3>{service.name}</h3>
                <p>{service.priceRange}</p>
                <Link to={`/booking/${service._id}`} className="book-now-btn">
                  Book Now
                </Link>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
};

export default Services;
