import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../../styles/Booking.css';

const Booking = () => {
    const [services, setServices] = useState([]);
    const [stylists, setStylists] = useState([]);
    const [formData, setFormData] = useState({
        service: '',
        stylist: '',
        locationType: 'Salon',
        dateTime: '',
    });
    const [availableSlots, setAvailableSlots] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        // Fetch services and stylists
        axios.get('http://localhost:5001/api/services') // Assume this endpoint exists
            .then((response) => setServices(response.data))
            .catch(() => setError('Failed to load services'));

        axios.get('http://localhost:5001/api/stylists') // Assume this endpoint exists
            .then((response) => setStylists(response.data))
            .catch(() => setError('Failed to load stylists'));
    }, []);

    const handleDateChange = async (e) => {
        const date = e.target.value;
        setFormData({ ...formData, dateTime: date });
        try {
            const response = await axios.get(`http://localhost:5001/api/bookings/availability?date=${date}`);
            setAvailableSlots(response.data.allSlots);
        } catch (err) {
            setError('Failed to fetch availability');
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');
        try {
            await axios.post(
                'http://localhost:5001/api/bookings',
                formData,
                { headers: { Authorization: `Bearer ${token}` } }
            );
            alert('Booking created successfully!');
            setFormData({ service: '', stylist: '', locationType: 'Salon', dateTime: '' });
        } catch (err) {
            setError('Failed to create booking');
        }
    };

    return (
        <div className="booking-page">
            <h1>Book an Appointment</h1>
            {error && <p className="error">{error}</p>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Service:</label>
                    <select
                        value={formData.service}
                        onChange={(e) => setFormData({ ...formData, service: e.target.value })}
                    >
                        <option value="">Select a service</option>
                        {services.map((service) => (
                            <option key={service._id} value={service.name}>{service.name}</option>
                        ))}
                    </select>
                </div>
                <div>
                    <label>Stylist:</label>
                    <select
                        value={formData.stylist}
                        onChange={(e) => setFormData({ ...formData, stylist: e.target.value })}
                    >
                        <option value="">Select a stylist</option>
                        {stylists.map((stylist) => (
                            <option key={stylist._id} value={stylist._id}>{stylist.username}</option>
                        ))}
                    </select>
                </div>
                <div>
                    <label>Location:</label>
                    <select
                        value={formData.locationType}
                        onChange={(e) => setFormData({ ...formData, locationType: e.target.value })}
                    >
                        <option value="Salon">Salon</option>
                        <option value="Home">Home Service</option>
                    </select>
                </div>
                <div>
                    <label>Date & Time:</label>
                    <input
                        type="datetime-local"
                        value={formData.dateTime}
                        onChange={handleDateChange}
                    />
                </div>
                <button type="submit">Book Now</button>
            </form>
        </div>
    );
};

export default Booking;
