import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection
import '../styles/Header.css';

const Header = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);

    useEffect(() => {
        // Check if user is logged in using localStorage
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            setUser(JSON.parse(storedUser));
        }
    }, []);

    const handleBookAppointmentClick = (e) => {
        if (!user) {
            e.preventDefault();
            navigate('/'); // Redirect to login page if not logged in
        }
        // If user is logged in, the default link behavior (to /appointment) will proceed
    };

    return (
        <header className="header">
            <h1>Welcome to The Moonâ€™s Salon</h1>
            <p>Your one-stop destination for beauty and wellness</p>
            <div className="cta-buttons">
                <a href="/appointment" onClick={handleBookAppointmentClick}>
                    Book an Appointment
                </a>
                <a href="/services">Explore Services</a>
            </div>
        </header>
    );
};

export default Header;
