import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import '../styles/Navbar.css';

import menuIcon from '../assets/icons/menu.png';
import cartIcon from '../assets/icons/shopping-cart.png';
import userIcon from '../assets/icons/user.png';
import moonLogo from '../assets/icons/moon.svg';

const Navbar = ({ openModal }) => {
    const navigate = useNavigate();
    const location = useLocation();
    const [user, setUser] = useState(null);

    useEffect(() => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            setUser(JSON.parse(storedUser));
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        setUser(null);
        navigate('/');
    };

    const handleLogoClick = () => {
        if (location.pathname === '/') {
            window.location.reload();
        } else {
            navigate('/');
        }
    };

    const handleProfileClick = () => {
        if (user) {
            navigate('/profile'); // ✅ Navigate to Profile page if logged in
        } else {
            openModal(); // ✅ Open login modal if not logged in
        }
    };

    return (
        <nav className="navbar">
            <div className="nav-left">
                <div className="dropdown-container">
                    <img src={menuIcon} alt="Menu Icon" className="menu-icon" />
                    <ul className="dropdown-menu">
                        <li><Link to="/services">Services</Link></li>
                        <li><Link to="/user/about">About Us</Link></li>
                        <li><Link to="/user/clients">Our Clients</Link></li>
                        <li><Link to="/user/contact">Contact Info</Link></li>
                    </ul>
                </div>
            </div>

            <div className="nav-center" onClick={handleLogoClick} style={{ cursor: 'pointer' }}>
                <img src={moonLogo} alt="Moon's Salon Logo" className="nav-logo" />
            </div>

            <div className="nav-right">
                <Link to="/store" className="icon-link">
                    <img src={cartIcon} alt="Store Icon" className="nav-icon" />
                </Link>

                {user ? (
                    <div className="user-info">
                        <span>Welcome, {user.username || user.email}</span>
                        <button onClick={handleProfileClick} className="profile-button">Profile</button>
                        <button onClick={handleLogout}>Logout</button>
                    </div>
                ) : (
                    <div onClick={handleProfileClick} className="icon-link" style={{ cursor: 'pointer' }}>
                        <img src={userIcon} alt="Profile Icon" className="nav-icon" />
                    </div>
                )}
            </div>
        </nav>
    );
};

export default Navbar;
