import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../../styles/Profile.css';

const Profile = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [error, setError] = useState('');

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/'); // Redirect to home if no token (modal will open there)
            return;
        }

        axios
            .get('http://localhost:5001/api/auth/profile', {
                headers: { Authorization: `Bearer ${token}` },
            })
            .then((response) => {
                setUser(response.data);
                setError('');
            })
            .catch((error) => {
                console.error('Error fetching profile:', error);
                setError('Failed to load profile. Please log in again.');
                localStorage.removeItem('token');
                localStorage.removeItem('user');
                navigate('/');
            });
    }, [navigate]);

    if (!user && !error) return <p>Loading profile...</p>;
    if (error) return <p style={{ color: 'red' }}>{error}</p>;

    return (
        <div className="profile-container">
            <h1>Welcome, {user.username}</h1>
            <div className="profile-info">
                <img
                    src="https://via.placeholder.com/150"
                    alt="Profile"
                    className="profile-picture"
                />
                <p><strong>Email:</strong> {user.email}</p>
                <p><strong>Role:</strong> {user.role}</p>
                <h2>About Me</h2>
                <p>
                    Hello! I‚Äôm {user.username}, a valued client of Moon's Salon. I enjoy
                    our premium beauty services!
                </p>
            </div>
            <div className="recent-activities">
                <h2>Recent Activities</h2>
                <ul>
                    <li>‚úîÔ∏è Logged in successfully</li>
                    <li>üìÖ Last visit: {new Date().toLocaleDateString()}</li>
                    <li>üíá‚Äç‚ôÄÔ∏è Booked a haircut (placeholder)</li>
                </ul>
            </div>
            <button
                onClick={() => {
                    localStorage.removeItem('token');
                    localStorage.removeItem('user');
                    navigate('/');
                }}
            >
                Logout
            </button>
        </div>
    );
};

export default Profile;
