import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Navbar.css';
import menuIcon from '../assets/icons/menu.png';
import cartIcon from '../assets/icons/shopping-cart.png';
import userIcon from '../assets/icons/user.png';

const Navbar = ({ openModal }) => {
    const [isVisible, setIsVisible] = useState(false);
    const [lastScrollY, setLastScrollY] = useState(window.scrollY);

    useEffect(() => {
        let timeout;

        const handleScroll = () => {
            if (window.scrollY > lastScrollY || window.scrollY < 10) {
                setIsVisible(false);  // Hide when scrolling down or at top
            } else {
                setIsVisible(true);   // Show when scrolling up
            }
            setLastScrollY(window.scrollY);

            // Hide navbar after 2 seconds of inactivity
            clearTimeout(timeout);
            timeout = setTimeout(() => {
                setIsVisible(false);
            }, 2000);
        };

        const handleMouseMove = (e) => {
            if (e.clientY <= 50) {
                setIsVisible(true); // Show when cursor is at the top
            }
        };

        window.addEventListener('scroll', handleScroll);
        window.addEventListener('mousemove', handleMouseMove);

        return () => {
            window.removeEventListener('scroll', handleScroll);
            window.removeEventListener('mousemove', handleMouseMove);
            clearTimeout(timeout);
        };
    }, [lastScrollY]);

    return (
        <nav className={`navbar ${isVisible ? 'visible' : 'hidden'}`}>
            {/* Left Side: Menu Dropdown */}
            <div className="nav-left">
                <div className="dropdown-container">
                    <img src={menuIcon} alt="Menu Icon" className="menu-icon" />
                    <ul className="dropdown-menu">
                        <li><Link to="/user/services">Services</Link></li>
                        <li><Link to="/user/about">About Us</Link></li>
                        <li><Link to="/user/clients">Our Clients</Link></li>
                        <li><Link to="/user/contact">Contact Info</Link></li>
                    </ul>
                </div>
            </div>

            {/* Center: Moon's Saloon Title */}
            <div className="nav-center">
                <Link to="/" className="nav-logo">Moonâ€™s Saloon</Link>
            </div>

            {/* Right Side: Store and Profile Icons */}
            <div className="nav-right">
                <Link to="/user/store" className="icon-link">
                    <img src={cartIcon} alt="Store Icon" className="nav-icon" />
                </Link>
                <div onClick={openModal} className="icon-link" style={{ cursor: 'pointer' }}>
                    <img src={userIcon} alt="Profile Icon" className="nav-icon" />
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
