// src/pages/Admin/AdminService.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../../styles/AdminService.css';

const AdminService = () => {
    const [services, setServices] = useState([]);
    const [newService, setNewService] = useState({
        name: '',
        description: '',
        priceRange: '',
        image: null,
    });

    useEffect(() => {
        // Fetch services when the component mounts
        fetchServices();
    }, []);

    const fetchServices = async () => {
        try {
            const res = await axios.get('http://localhost:5000/api/services');
            setServices(res.data.map(service => ({
                ...service,
                imageUrl: service.imageUrl || 'default-image-url.jpg', // Provide default if image doesn't exist
            })));
        } catch (error) {
            console.error('Error fetching services:', error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setNewService(prev => ({
            ...prev,
            [name]: value,
        }));
    };

    const handleFileChange = (e) => {
        setNewService(prev => ({
            ...prev,
            image: e.target.files[0],
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('name', newService.name);
        formData.append('description', newService.description);
        formData.append('priceRange', newService.priceRange);
        formData.append('image', newService.image);

        try {
            await axios.post('http://localhost:5000/api/services', formData, {
                headers: { 'Content-Type': 'multipart/form-data' },
            });
            setNewService({ name: '', description: '', priceRange: '', image: null });
            fetchServices();
        } catch (error) {
            console.error('Error adding service:', error);
        }
    };

    const handleDelete = (id) => {
        if (window.confirm('Are you sure you want to delete this service?')) {
            axios.delete(`http://localhost:5000/api/services/${id}`)
                .then(() => setServices(services.filter(service => service._id !== id)))
                .catch(error => console.error('Error deleting service:', error));
        }
    };

    return (
        <div>
            <h1>Manage Services</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="name"
                    placeholder="Service Name"
                    value={newService.name}
                    onChange={handleChange}
                    required
                />
                <textarea
                    name="description"
                    placeholder="Description"
                    value={newService.description}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="priceRange"
                    placeholder="Price Range"
                    value={newService.priceRange}
                    onChange={handleChange}
                    required
                />
                <input
                    type="file"
                    onChange={handleFileChange}
                    required
                />
                <button type="submit">Add Service</button>
            </form>

            <div>
                {services.map(service => (
                    <div key={service._id}>
                        <h3>{service.name}</h3>
                        <p>{service.description}</p>
                        <p>{service.priceRange}</p>
                        <img src={service.imageUrl} alt={service.name} />
                        <button onClick={() => handleDelete(service._id)}>Delete</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default AdminService;
