import React, { useState } from 'react';
import Navbar from '../../components/Navbar';
import Footer from '../../components/Footer';
import '../../styles/Clients.css';

// ✅ Import images directly from assets
import client1 from '../../assets/images/client1.jpeg';
import client2 from '../../assets/images/client2.jpeg';
import client3 from '../../assets/images/client3.jpeg';
import client4 from '../../assets/images/client4.png'; // PNG for client4
import client5 from '../../assets/images/client5.png'; // PNG for client5

const clientReviews = [
    { 
        name: "Sophia Carter", 
        review: "I had an amazing experience at Moon's Salon! The staff was incredibly attentive, and the ambiance was so relaxing. Highly recommend this place!", 
        image: client1 
    },
    { 
        name: "Michael Brown", 
        review: "Moon's Salon offers top-notch service with a great atmosphere. I visited for a haircut and beard trim, and the attention to detail was impressive.", 
        image: client2 
    },
    { 
        name: "Emily Johnson", 
        review: "Loved my experience at Moon's Salon—it’s truly an elegant salon! My stylist delivered a stunning haircut, and the products left my hair feeling silky.", 
        image: client3 
    },
    { 
        name: "Alex Rodriguez", 
        review: "I was blown away by the professionalism at Moon's Salon. My color treatment turned out perfectly, and the staff was knowledgeable and kind.", 
        image: client4 
    },
    { 
        name: "Olivia Davis", 
        review: "Moon's Salon exceeded my expectations! The haircut and styling were flawless, and the consultation process was thorough.", 
        image: client5 
    }
];

const Clients = () => {
    const [showMore, setShowMore] = useState(false);

    const handleShowMore = () => {
        setShowMore(!showMore);
    };

    return (
        <div className="clients-page">
            <Navbar />
            <div className="clients-header">
                <h1>Our Happy Clients</h1>
                <p>See what our clients have to say about Moon's Salon.</p>
            </div>

            <div className="client-reviews">
                {clientReviews.slice(0, 3).map((client, index) => (
                    <div className="review-card" key={index}>
                        <img src={client.image} alt={client.name} className="client-image" />
                        <h3>{client.name}</h3>
                        <p>{client.review}</p>
                    </div>
                ))}
            </div>

            {clientReviews.length > 3 && (
                <div className="more-reviews-section">
                    <button className="show-more-btn" onClick={handleShowMore}>
                        {showMore ? 'Hide Reviews' : 'Show More Reviews'}
                    </button>

                    {showMore && (
                        <div className={`additional-reviews ${showMore ? 'slide-in' : ''}`}>
                            {clientReviews.slice(3, 5).map((client, index) => (
                                <div className="review-card additional" key={index + 3}>
                                    <img src={client.image} alt={client.name} className="client-image" />
                                    <h3>{client.name}</h3>
                                    <p>{client.review}</p>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            )}

            <Footer />
        </div>
    );
};

export default Clients;
