import React from 'react';
import Navbar from '../../components/Navbar';
import Footer from '../../components/Footer';
import '../../styles/Clients.css';

// ✅ Import images directly from assets
import client1 from '../../assets/images/client1.jpeg';
import client2 from '../../assets/images/client2.jpeg';
import client3 from '../../assets/images/client3.jpeg';
import client4 from '../../assets/images/client4.jpeg'; // Add a new image if needed
import client5 from '../../assets/images/client5.jpeg'; // Add another new image if needed

const clientReviews = [
    { 
        name: "Sophia Carter", 
        review: "I had an amazing experience at Moon's Salon! The staff was incredibly attentive, and the ambiance was so relaxing. My hair transformation was beyond my expectations—I left feeling confident and beautiful. Highly recommend this place to anyone looking for top-notch service!", 
        image: client1 
    },
    { 
        name: "Michael Brown", 
        review: "Moon's Salon offers top-notch service with a great atmosphere. I visited for a haircut and beard trim, and the attention to detail was impressive. The team was friendly, and the salon felt luxurious yet welcoming. I’ll definitely be back for more!", 
        image: client2 
    },
    { 
        name: "Emily Johnson", 
        review: "Loved my experience at Moon's Salon—it’s truly an elegant salon! My stylist took the time to understand my preferences and delivered a stunning haircut. The products they used left my hair feeling silky and healthy. I can’t wait to return for their spa treatments!", 
        image: client3 
    },
    { 
        name: "Alex Rodriguez", 
        review: "I was blown away by the professionalism at Moon's Salon. From the moment I walked in, I felt pampered. My color treatment turned out perfectly, and the staff was knowledgeable and kind. It’s now my go-to salon for all my beauty needs!", 
        image: client4 // Assuming you have this image
    },
    { 
        name: "Olivia Davis", 
        review: "Moon's Salon exceeded my expectations! The haircut and styling were flawless, and the consultation process was thorough. The salon’s decor is beautiful, and the staff made me feel so comfortable. I highly recommend their services to everyone!", 
        image: client5 // Assuming you have this image
    }
];

const Clients = () => {
    return (
        <div className="clients-page">
            <Navbar />
            <div className="clients-header">
                <h1>Our Happy Clients</h1>
                <p>See what our clients have to say about Moon's Salon.</p>
            </div>

            <div className="client-reviews">
                {clientReviews.map((client, index) => (
                    <div className="review-card" key={index}>
                        <img src={client.image} alt={client.name} className="client-image" />
                        <h3>{client.name}</h3>
                        <p>{client.review}</p> {/* Changed from quotes to plain paragraph for longer text */}
                    </div>
                ))}
            </div>

            <Footer />
        </div>
    );
};

export default Clients;
