// frontend/src/components/Profile.js
import React, { useEffect, useState, useContext } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import placeholderImage from '../../assets/images/placeholder.png';
import '../../styles/Profile.css';
import { AuthContext } from '../../context/AuthContext';

const Profile = () => {
  const { logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [bookings, setBookings] = useState([]);
  const [error, setError] = useState('');
  const [settingsMode, setSettingsMode] = useState(null); // New state for settings mode
  const [name, setName] = useState('');
  const [profilePicture, setProfilePicture] = useState(null);
  const [password, setPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/');
      return;
    }

    axios
      .get('http://localhost:5001/api/auth/me', {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((response) => {
        setUser(response.data);
        setName(response.data.username);
        setError('');
      })
      .catch((error) => {
        console.error('Error fetching profile:', error);
        setError('Failed to load profile. Please log in again.');
        logout();
        navigate('/');
      });

    axios
      .get('http://localhost:5001/api/bookings/user', {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((response) => {
        setBookings(response.data);
      })
      .catch((error) => {
        console.error('Error fetching bookings:', error);
        setError('Failed to load booking history.');
      });
  }, [navigate, logout]);

  const handleEditProfileClick = () => {
    setSettingsMode('editProfile');
  };

  const handleResetPasswordClick = () => {
    setSettingsMode('resetPassword');
  };

  const handleCancelClick = () => {
    setSettingsMode(null);
    setProfilePicture(null);
    setPassword('');
    setNewPassword('');
    setConfirmNewPassword('');
    setError('');
  };

  const handleSaveProfileClick = async () => {
    const token = localStorage.getItem('token');
    const formData = new FormData();
    if (name !== user.username) formData.append('username', name);
    if (profilePicture) formData.append('profilePicture', profilePicture);

    try {
      const response = await axios.post(
        'http://localhost:5001/api/user/update',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data',
          },
        }
      );

      const updatedUser = response.data;
      setUser(updatedUser);
      localStorage.setItem('user', JSON.stringify(updatedUser));
      setSettingsMode(null);
      setProfilePicture(null);
      setError('');
    } catch (error) {
      console.error('Error updating profile:', error.response?.data || error.message);
      setError('Failed to update profile. Please try again.');
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setProfilePicture(file);
    }
  };

  const handlePasswordChange = async () => {
    const token = localStorage.getItem('token');
    if (newPassword !== confirmNewPassword) {
      setError('New password and confirm password do not match.');
      return;
    }

    try {
      await axios.post(
        'http://localhost:5001/api/user/reset-password',
        { oldPassword: password, newPassword },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setError('Password reset successful');
      setPassword('');
      setNewPassword('');
      setConfirmNewPassword('');
      setSettingsMode(null);
    } catch (error) {
      console.error('Error resetting password:', error.response?.data || error.message);
      setError('Failed to reset password. Please check your current password.');
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  if (!user && !error) return <p>Loading profile...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div className="profile-container">
      <h1>Welcome, {user.username}</h1>
      <div className="profile-info">
        <img
          src={user.profilePicture || placeholderImage}
          alt="Profile"
          className="profile-picture"
        />
        <p><strong>Username:</strong> {user.username}</p>
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Role:</strong> {user.role}</p>

        <h2>Settings</h2>
        {settingsMode === null ? (
          <div className="settings-options">
            <button onClick={handleEditProfileClick} className="edit-button">
              Edit Profile
            </button>
            <button onClick={handleResetPasswordClick} className="reset-password-button">
              Reset Password
            </button>
          </div>
        ) : settingsMode === 'editProfile' ? (
          <div className="edit-profile-section">
            <input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
              className="profile-picture-input"
            />
            {profilePicture ? (
              <img
                src={URL.createObjectURL(profilePicture)}
                alt="Profile Preview"
                className="profile-picture"
              />
            ) : (
              <img
                src={user.profilePicture || placeholderImage}
                alt="Profile"
                className="profile-picture"
              />
            )}
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="edit-input"
            />
            <button onClick={handleSaveProfileClick} className="save-button">
              Save
            </button>
            <button onClick={handleCancelClick} className="cancel-button">
              Cancel
            </button>
          </div>
        ) : (
          <div className="reset-password-section">
            <h3>Reset Password</h3>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Current Password"
              className="password-input"
            />
            <input
              type="password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              placeholder="New Password"
              className="password-input"
            />
            <input
              type="password"
              value={confirmNewPassword}
              onChange={(e) => setConfirmNewPassword(e.target.value)}
              placeholder="Confirm New Password"
              className="password-input"
            />
            <button onClick={handlePasswordChange} className="save-button">
              Submit
            </button>
            <button onClick={handleCancelClick} className="cancel-button">
              Cancel
            </button>
          </div>
        )}
      </div>

      <div className="booking-history">
        <h2>Booking History</h2>
        {bookings.length === 0 ? (
          <p>No bookings yet.</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Service</th>
                <th>Stylist</th>
                <th>Date/Time</th>
                <th>Location</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {bookings.map((booking) => (
                <tr key={booking._id}>
                  <td>{booking.service}</td>
                  <td>{booking.stylist?.username || 'N/A'}</td>
                  <td>{new Date(booking.dateTime).toLocaleString()}</td>
                  <td>{booking.locationType}</td>
                  <td>{booking.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>

      <div className="purchases-section">
        <h2>Purchases</h2>
        <p>
          No purchases yet. Visit the <Link to="/store">store</Link> to explore products!
