import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Profile = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login"); // Redirect to login if not authenticated
      return;
    }

    axios.get("http://localhost:5000/api/user/profile", {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(response => {
        setUser(response.data);
      })
      .catch(error => {
        console.error("Error fetching profile:", error);
        localStorage.removeItem("token");
        navigate("/login");
      });
  }, [navigate]);

  if (!user) return <p>Loading profile...</p>;

  return (
    <div className="profile-container">
      <h1>Welcome, {user.username}</h1>
      <p><strong>Email:</strong> {user.email}</p>

      {/* Static Profile Section */}
      <div className="profile-info">
        <img 
          src="https://via.placeholder.com/150" 
          alt="Profile" 
          className="profile-picture"
        />
        <h2>About Me</h2>
        <p>Hello! I am a user of this amazing platform. I enjoy exploring new features and making the most of my experience.</p>
      </div>

      {/* Static Recent Activities Section */}
      <div className="recent-activities">
        <h2>Recent Activities</h2>
        <ul>
          <li>‚úîÔ∏è Logged in successfully</li>
          <li>üìÖ Last profile update: 3 days ago</li>
          <li>üîî Subscribed to email notifications</li>
        </ul>
      </div>

      <button onClick={() => {
        localStorage.removeItem("token");
        navigate("/login");
      }}>Logout</button>
    </div>
  );
};

export default Profile;
