import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../../styles/AdminStylists.css';

const AdminStylists = () => {
  const [stylists, setStylists] = useState([]);
  const [formData, setFormData] = useState({ username: '', email: '', password: '' });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const token = localStorage.getItem('token');

  const fetchStylists = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/users/stylists', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setStylists(response.data);
      setError('');
    } catch (err) {
      console.error('Error fetching stylists:', err);
      setError(err.response?.data?.message || 'Failed to load stylists.');
    }
  };

  useEffect(() => {
    fetchStylists();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        'http://localhost:5001/api/users/create-stylist',
        formData,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setSuccess(response.data.message || 'Stylist created successfully!');
      setFormData({ username: '', email: '', password: '' });
      fetchStylists();
    } catch (err) {
      console.error('Error creating stylist:', err);
      setError(err.response?.data?.message || 'Failed to create stylist.');
      setSuccess('');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this stylist?")) return;
    try {
      await axios.delete(`http://localhost:5001/api/users/stylist/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Stylist deleted!');
      fetchStylists();
    } catch (err) {
      console.error('Error deleting stylist:', err);
      alert(err.response?.data?.message || 'Failed to delete stylist.');
    }
  };

  const handleEdit = async (id) => {
    const newUsername = prompt("Enter new username:");
    const newEmail = prompt("Enter new email:");
    if (!newUsername || !newEmail) return;
    try {
      await axios.put(
        `http://localhost:5001/api/users/stylist/${id}`,
        { username: newUsername, email: newEmail },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Stylist updated successfully!');
      fetchStylists();
    } catch (err) {
      console.error('Error updating stylist:', err);
      alert(err.response?.data?.message || 'Failed to update stylist.');
    }
  };

  const handleResetPassword = async (id) => {
    const newPassword = prompt("Enter new password:");
    if (!newPassword) return;
    try {
      await axios.put(
        `http://localhost:5001/api/users/stylist/${id}/reset-password`,
        { password: newPassword },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Password reset successfully!');
    } catch (err) {
      console.error('Error resetting password:', err);
      alert(err.response?.data?.message || 'Failed to reset password.');
    }
  };

  return (
    <div className="admin-stylists">
      <h1>Manage Stylists</h1>
      {error && <p className="error">{error}</p>}
      {success && <p className="success">{success}</p>}

      <form onSubmit={handleSubmit} className="create-stylist-form">
        <h2>Create New Stylist</h2>
        <input
          type="text"
          placeholder="Username"
          value={formData.username}
          onChange={(e) => setFormData({ ...formData, username: e.target.value })}
          required
        />
        <input
          type="email"
          placeholder="Email"
          value={formData.email}
          onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={formData.password}
          onChange={(e) => setFormData({ ...formData, password: e.target.value })}
          required
        />
        <button type="submit">Create Stylist</button>
      </form>

      <h2>Existing Stylists</h2>
      <table className="stylists-table">
        <thead>
          <tr>
            <th>Username</th>
            <th>Email</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {stylists.map((stylist) => (
            <tr key={stylist._id}>
              <td>{stylist.username}</td>
              <td>{stylist.email}</td>
              <td>
                <button onClick={() => handleEdit(stylist._id)}>Edit</button>
                <button onClick={() => handleDelete(stylist._id)}>Delete</button>
                <button onClick={() => handleResetPassword(stylist._id)}>Reset Password</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminStylists;
