import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './pages/User/Home';
import Services from './pages/User/Services'; // ✅ Import the Services page
import Navbar from './components/Navbar'; // ✅ Import the Navbar
import About from './pages/User/About';
import Clients from './pages/User/Clients';
import Contact from './pages/User/Contact';
import Profile from './pages/User/Profile'; // ✅ Import Profile Page
import Modal from './components/Modal'; // ✅ Import Modal

function App() {
    // Function to check if user is logged in
    const isAuthenticated = () => {
        return localStorage.getItem('token') !== null;
    };

    return (
        <Router>
            <Navbar /> {/* ✅ Ensure Navbar is always visible */}
            <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/services" element={<Services />} /> 
                <Route path="/user/about" element={<About />} />
                <Route path="/user/clients" element={<Clients />} />
                <Route path="/user/contact" element={<Contact />} />

                {/* ✅ Protected Profile Route */}
                <Route path="/profile" element={isAuthenticated() ? <Profile /> : <Navigate to="/" />} />
                <Route path="/login" element={<Modal isOpen={true} />} /> {/* ✅ Ensure /login route works properly */}
            </Routes>
        </Router>
    );
}

export default App;
